
public class 변수_선언과저장 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		/* 변수란 하나의 값을 저장할 수 있는 저장공간을 의미한다. */
		int x; // 정수(integer)를 저장하기 위한 변수 x 선언
		x = 5; // 변수 x에 5를 저장
		System.out.println(x);
		
		x = 3; // 변수 x에 3 값으로 갱신
		System.out.println(x);
		
		
		x = 10;
		int y = 5;
		
		System.out.println(x+y);
		System.out.println(x-y);
		System.out.println(x*y);
		System.out.println(x/y);
		System.out.println(x%y);
	}

}
