
public class 변수_타입 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		/* 변수의 타입은 
		 * 숫자는 int, long, float, double
		 * 	정수 : int, long
		 * 	실수 : float, double
		 * 문자는 char, String
		 * 	문자 : char
		 * 	문자열 : String
		 */
		int x = 100;
		double pi = 3.14;
		char ch = 'a';	// char은 ''으로 정의한다.
		String str = "abc";	// str은 ""으로 정의한다.
		
		System.out.println(x);
		System.out.println(pi);
		System.out.println(ch);
		System.out.println(str);
	}

}
