
public class 화면출력 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.print("Hello, world"); // 화면에 Hello, world 출력
		System.out.print(3+5); // 화면에 8 출력
		System.out.print("3+5"); // 화면에 3+5 출력
		System.out.println("Hello, world"); // 화면에 출력 후 한 줄 띄움
		System.out.println(3+5); // 화면에 8 출력 후 한 줄 띄움
		System.out.println("3+5"); // 화면에 3+5 출력 후 한 줄 띄움

	}

}
